module.exports = function(RED) {
    "use strict";
    function PrintMessage(config) {
        RED.nodes.createNode(this,config);
        const node = this;
        node.on('input',function(msg, send, done) {
            const varArr = config.variableName.split(",");
            for (let i = 0; i < varArr.length; i++) {
                const v = varArr[i];
                const r = msg[v]
                if (r !== undefined) {
                    // 提交显示
                    node.error(r)
                }
            }
            done()
        });
    }
    RED.nodes.registerType("debug-print",PrintMessage);
}
