// 获得属性名称
function domPropMap(RED){
    const map = {
        "id": "id",
        "name": RED._("node-red:browser.read.props.name"),
        "src": RED._("node-red:browser.read.props.src"),
        "href": RED._("node-red:browser.read.props.href"),
        "played": RED._("node-red:browser.read.props.played"),
        "alt": RED._("node-red:browser.read.props.alt"),
        "muted": RED._("node-red:browser.read.props.muted"),
    }

    this.getPropName = function (prop){
        const v = map[prop]
        return v ? v : prop
    }
}
