function array2Options(text){
    const options = []
    if( text !== undefined && text !== null && text !== "" ) {
        text.split(",").forEach(item => {
            options.push({
                value: item,
                label: item
            })
        })
    }
    return options
}
function InitReadSelectedComponents(domName, options){
    const _o = $(domName)
    _o.typedInput({
        types: [
            {
                value: "",
                multiple: "true",
                options: options || []
            }
        ]
    })
    /*
    const _this = this
    _o.on("_Showed", function(e){
        const component = _this._parent.components[_this._name]
        component.updateOptions(_this['rpaData'].css)
    })
    */
    this._o = _o
    return this
}

InitReadSelectedComponents.prototype.updateOptions = function(tryOptions) {
    const options = []
    const keys = Object.keys(tryOptions)
    if( keys && keys.length > 0 ){
        for(let i=0; i<keys.length; i++){
            const key = keys[i]
            options.push({
                value: key,
                label: key
            })
        }
    }
    this._o.typedInput("updateOptions", options)
    return this
}

