function initInputImageControl(imageControlObjectArray){
    for(let imagePair of imageControlObjectArray){
        const {text, input, view} = imagePair;
        const textDom = $(text)
        const viewDom = $(view)
        textDom.change(function(){
            viewDom.attr('src', textDom.val());
        })
        $(input).change(function (){
            const fileAddr = this.files[0].path;
            textDom.val(fileAddr);
            textDom.trigger('change');
        });
        viewDom.attr('src', textDom.val());
    }
}
