// 单选
/*
{
    value:"order",
    component: "#node-input-selectOrderType",
    options: [
        { value: "order", label: "browser.label.select-order-by"), component: ["#select-order-by"] },
    ],
}
*/
function initSelectPlane(RED, config) {
    // 初始化选择器类型
    const defaultOptions = []
    for(let item of config.options){
        defaultOptions.push({
            value: item.value,
            label: RED._("node-red:" + item.label),
        })
    }
    const component = $(config.component)
    let current = config.value
    component.typedInput({
        types: [{
            value: current,
            options: defaultOptions
        }]
    })
    const dependencies = config.dependencies
    component.on("change", function(){
        const val = $(this).val()
        // if( dependencies.length === 0 || !$(dependencies).is(":hidden") ){
            _VisitComponent(config.options, current, false)
            _VisitComponent(config.options, val, true)
        // }
        current = val
    })
}
function _VisitComponent(components, name, show = true){
    const options = components.find(item => item.value === name)
    if( options ){
        const component = $(options.component.join(","))
        show ? component.show() : component.hide();
    }
}
